mapper.select:
SELECT cartid, itemid, quantity, totalPrice, price
FROM cartitem
WHERE cartid = #{cartId}

mapper.update:
UPDATE cartitem
SET quantity = #{quantity},
    totalPrice = #{totalPrice}
WHERE cartid = #{cartId} AND itemid = #{itemId}

