mapper.select:
SELECT *
FROM account
WHERE email = #{email}
  AND password = #{password}

mapper.update:
UPDATE account
SET email = #{newEmail}
WHERE userid = #{userid}

UPDATE account
SET password = #{newPassword}
WHERE userid = #{userid}

UPDATE account
SET VIPLevel = #{VIPLevel}
WHERE userid = #{userid}

UPDATE account
SET avatar_url = #{avatar_url}
WHERE userid = #{userid}

UPDATE account
SET login_type = #{login_type}
WHERE userid = #{userid}

UPDATE account
SET token_version = #{token_version}
WHERE userid = #{userid}
